
public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int jerry=5;
		int tom= 3;
		int temp;
		System.out.println("No.of apples jeery have "+jerry);
		System.out.println("No.of apples tom have "+tom);
		temp=jerry;
		jerry=tom;
		tom=temp;
		System.out.println("No.of apples jeery have after swaping "+jerry);
		System.out.println("No.of apples tom have after swaping  "+tom);
	}

}
